args.actor.setupSkill(game.i18n.localize("NAME.Cool"), {absolute : {difficulty : "average"}, context : {failure : "Suffer Creeping Irrationality"}}).then(setupData => {
    args.actor.basicTest(setupData).then(test => {
        if (test.result.outcome == "failure")
        {
            msg = `Creeping Irratitonality Roll: ${(await new Roll("1d10").roll()).total}`
            if (test.result.outcome.roll % 11 == 0 || test.result.outcome.roll == 100)
            {
                msg += `<br><b>${args.actor.prototypeToken.name}</b> also gains 1 Corruption Point. If mutating, this results in a @Table[mutatemental]{Mental Corruption}`
                let newCorruption = Number(args.actor.status.corruption.value) + 1
                args.actor.update({"system.status.corruption.value" : newCorruption})
                args.actor.checkCorruption()
            }

            ChatMessage.create({content : msg, speaker : {alias : this.effect.name}})
        }

    })
})
